import React, {Fragments} from "react";
// material-ui components
import withStyles from "@material-ui/core/styles/withStyles";
// core components
import Card from "../Card/Card.jsx";
import CardBody from "../Card/CardBody.jsx";
import CardHeader from "../Card/CardHeader.jsx";
import Button from "../CustomButtons/Button.jsx";
import PropertiesCard from "../body/PropertiesCard"
import BookDetails from "../BookDetails"
import imagesStyles from "../../assets/jss/material-kit-react/imagesStyles.jsx";
import Divider from '@material-ui/core/Divider';

import { cardTitle } from "../../assets/jss/material-kit-react.jsx";

import W from "./W.jpeg"

const style = {
  ...imagesStyles,
  cardTitle,
  textMuted: {
    color: "#6c757d"
  },
};

class Cards extends React.Component {
  constructor(props){
    super(props)

    this.state= {
      showDetails:false
    }

    this.handleClick = this.handleClick.bind(this)
  }

  mouseHover = (event) => {
      return (
        console.log(this.props.id)
      )
  }

  handleClick = () => {
    return(
      this.setState({
        showDetails: true
      })
    )
  }

  detailsClose = () => {
    return(
      this.setState({
        showDetails: false
      })
    )
  }

  render() {
    const { classes } = this.props;
    var data = this.props;
    return (
      <div>
      <Card style={{width: "20rem" , position: "relative"}}>
        <CardHeader color="none" style={{borderRadius:100}} onMouseOver={
          (e) => {this.mouseHover()}}>
          <img src={W} width="260px" height="60px"/>
        </CardHeader>
        <CardBody>
          <h4 className={classes.cardTitle}>{data.title}</h4>
          <p>
            {data.ts}
          </p>
          <Button
          color="primary"
          onClick={(e) => {this.handleClick(e, data)}}
          >See Full Post
          </Button>
        </CardBody>
      </Card>
      {
        this.state.showDetails === true ?
         <BookDetails open={true} book={data} close={this.detailsClose}/> :
         <Divider />
      }


      </div>
    );
  }
}


export default withStyles(style)(Cards);
