import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';

import Home from '@material-ui/icons/Home';
import TrendingUp from '@material-ui/icons/TrendingUp';
import LibraryBooks from '@material-ui/icons/LibraryBooks';
import WatchLater from '@material-ui/icons/WatchLater';
import Delete from '@material-ui/icons/Delete';

import InputBase from '@material-ui/core/InputBase';
import SearchIcon from '@material-ui/icons/Search';
import { fade } from '@material-ui/core/styles/colorManipulator';
import ContainedButtons from './navabuttons'
import BookList from '../BookList';
import Paper from '@material-ui/core/Paper'
import W from "./W.jpeg"
import bgImg from "./S.jpg"
import Profile from './profile';

import Grid from "@material-ui/core/Grid"

const drawerWidth = 240;

const styles = theme => ({
  root: {
    display: 'flex',
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px) + '5rem'`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },

  menuButton: {
    marginLeft: 12,
    marginRight: 36,
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: 'nowrap',
  },
  drawerOpen: {
    width: drawerWidth,
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },

  drawerClose: {
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    overflowX: 'hidden',
    width: theme.spacing.unit * 7 + 1,
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing.unit * 9 + 1,
    },
  },
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    //justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing.unit * 3,
  },
  profileImgCtn: {
    marginTop : "0.5rem",
    marginLeft : "0.5rem",
    height: 50,
    width: 50,
    borderRadius: 25,
    Shadow:20
  },
  profileImg: {
    height: 50,
    width: 50,
    borderRadius: 25,
    Shadow:20
  },
});

class MiniDrawer extends React.Component {
  state = {
    open: false,
    variant : "permanent",
    isMobile : false,
    search:"Elon Musk",
    renderChange: 'profile',
  };

  handleDrawerOpen = () => {
    this.setState({ open: true });
  };

  handleDrawerClose = () => {
    this.setState({ open: false });
  };

  mouseInDrawer = (event)=>{
    this.setState({
      open: true
    })
  }

  mouseOutDrawer = (event)=>{
    this.setState({
      open: false
    })
  }

  componentDidMount(){
    if(window.innerWidth<768){
      this.setState({
        variant:""
      })
    }
  }

  render() {
    const isMobile = this.state.width <= 768
    const { classes, theme } = this.props;

    return (
      <div className={classes.root}>
        <AppBar
          style={{ background: 'transparent', boxShadow: 'none'}}
          position="fixed"
          className={classNames(classes.appBar, {
            [classes.appBarShift]: this.state.open,
          })}
        >
          <Toolbar disableGutters={!this.state.open}>
            { isMobile ?
              <div>
              <IconButton
                render={this.state.isMobile}
                color="inherit"
                aria-label="Open drawer"
                onClick={this.handleDrawerOpen}
                className={classNames(classes.menuButton, {
                  [classes.hide]: this.state.open,
                })}
              >
                <MenuIcon render={this.state.isMobile}/>
              </IconButton>
              <Typography variant="h6" color="inherit" noWrap>
                BookSTack
              </Typography>
            </div>:
            <Typography style={{marginLeft:"5rem"}} variant="h6" color="black" noWrap>
              BookSTack
            </Typography>
            }


            <div className={classes.search}>
              <div className={classes.searchIcon} onClick={this.handleClick}>
                <IconButton disabled={false} disableRipple={false}>
                  <SearchIcon />
                </IconButton>
              </div>
              <InputBase
                placeholder="Searchâ€¦"
                classes={{
                  root: classes.inputRoot,
                  input: classes.inputInput,
                }}
                onChange={this.handleChange}
              />
            </div>
            <div style={{flex:1}} >
            <ContainedButtons />
            </div>
          </Toolbar>
        </AppBar>

        <Drawer
          variant = {this.state.variant}
          className={classNames(classes.drawer, {
            [classes.drawerOpen]: this.state.open,
            [classes.drawerClose]: !this.state.open,
          })}
          classes={{
            paper: classNames({
              [classes.drawerOpen]: this.state.open,
              [classes.drawerClose]: !this.state.open,
            }),
          }}
          open={this.state.open}
          onMouseEnter={this.mouseInDrawer}
          onMouseLeave={this.mouseOutDrawer}
        >
        <div
          className={classes.background}
          style={{ backgroundImage: "url(./S.jpg)" }}
        />

          <Grid container cols={2}>
          <Grid item>
          <Paper elevation={8} className={classes.profileImgCtn}><img
          className={classes.profileImg}
          src={W}/></Paper>
          </Grid>
          <Grid item>
          <h3 style={{position:"absolute", marginLeft:"1rem",marginTop:"1rem"}}>UserName</h3>
          </Grid>
          </Grid>
          <Divider style={{marginTop:"1rem"}}/>

          <List>
              <ListItem button key='Home'>
                <ListItemIcon>
                <Home />
                </ListItemIcon>
                <ListItemText primary= 'Home'/>
              </ListItem>
              <ListItem button key='Trending'>
                <ListItemIcon>
                <TrendingUp />
                </ListItemIcon>
