import React from 'react';
import TextField from '@material-ui/core/TextField';
import AddIcon from '@material-ui/icons/Add';
import Fab from '@material-ui/core/Fab';
import Button from '@material-ui/core/Button';
import { withStyles } from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import Fade from '@material-ui/core/Fade';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid'
import CustomInput from "../CustomInput/CustomInput.jsx";

const styles = theme => ({
  root: {
    height: 180,
  },
  wrapper: {
    width: 100 + theme.spacing.unit * 2,
  },
  paper: {
    zIndex: 1,
    position: 'absolute',
    margin: theme.spacing.unit,
  },
});

class AddPost extends React.Component {
  state = {
    checked: false,
    title:'',
    content:'',
  };

  handleChange = () => {
    this.setState(state => ({ checked: !state.checked }));
  };

  saveForm = (event) => {
    console.log(event.target)
    this.setState({
      [event.target.getAttribute('name')]: event.target.value,
    });
  };



  render() {
    const { classes } = this.props;
    const width = window.innerWidth - 190
    const height = window.innerHeight - 400

    return (
      <div>
      <div className={classes.root}>
        <div className={classes.wrapper}>
          <Fade direction="left" in={this.state.checked} mountOnEnter unmountOnExit>
            <Paper  elevation={7} className={classes.paper} style={{marginLeft:"10vw",height:"80vh", width:"80vw"}}>
              <Grid item>
              <Paper>
                <form>
                <Grid container xs={12} sm={12} md={12}>
                  <Grid item xs={12} sm={12} md={12}>
                  <TextField
                  onChange={this.saveForm}
                  autoFocus={true}
                  name='title'
                  type="text"
                  placeholder="Enter Post Title"
                  variant="standard"
                  fullWidth={true}
                  />
                  </Grid>
                  <Grid item xs={12} sm={12} md={12}>
                  <TextField
                  onChange={this.saveForm}
                  name='content'
                  type="text"
                  placeholder="Enter Book Summary That You Made"
                  variant="outlined"
                  fullWidth={true}
                  multiline={true}
                  />
                  </Grid>
                  </Grid>
                </form>
              </Paper>
              </Grid>
            </Paper>
          </Fade>
        </div>
      </div>
      <Fab
          style={{borderRadius:"5rem",width: "10rem",height:"5rem",marginLeft:width,marginTop:height,}}
          variant="extended"
          color="primary"
          aria-label="Add"
          className={classes.margin}
          onClick={this.handleChange}
          aria-label="Collapse"
        >
          <AddIcon className={classes.extendedIcon} />
          Add Post
        </Fab>
        </div>

    );
  }
}


export default withStyles(styles)(AddPost)
