                  {tileData.map(tile => (
                    <GridListTile key={tile.img} cols={tile.cols || 1}>
                      <img src={tile.img} alt={tile.title} />
                    </GridListTile>
                  ))}
                </GridList>

                }
            }
        catch(err){console.log(err);}

    }
    render(){
        return(
            <div style={{marginTop:"12vh"}}>
                { this.displayBooks() }
            </div>
        );
    }
}

export default graphql(getBooksQuery,{
  options: (props)=>{
    return{
        variables:{
          search:props.search,
          howmany: 25
        }
    }
  }
})(BookList);
