import React, { Component } from 'react';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';
import { BrowserRouter as Router ,Route,Redirect } from 'react-router-dom'
import firebase from "firebase"


// components
import BaseRouter from './router.js'
import Navigation from './layout/Navigation'


//auth
var config = {
 apiKey: "AIzaSyBdD-dZnTara6z3jjKOENeqWQJz8Lk53jQ",
 authDomain: "fir-auth-b1ee0.firebaseapp.com",
};
firebase.initializeApp(config);
// apollo client setup
const client = new ApolloClient({
    uri: 'http://35.154.49.7:4000/graphql'
});



class App extends Component {

  state = {
    isSignedIn: false
  }

  auth = (data)=>  this.setState({authenticated:data})

  componentDidMount = () => {
            firebase.auth().onAuthStateChanged(user => {
              this.setState({ isSignedIn: !!user })
              console.log("user", user)
            })
          }


  render() {
    console.log("checking:" +this.state.isSignedIn+firebase.auth().currentUse);
    var name, email, photoUrl, uid, emailVerified;
    return (

        <ApolloProvider client={client}>
          <Router>
          <React.Fragment>
              <Navigation {...this.state}/>
              <h1>{name}??</h1>
              <h1>{email}??</h1>
              <BaseRouter {...this.props} />

            </React.Fragment>
          </Router>
        </ApolloProvider>
    );
  }
}
export default App
