import React, { Component } from 'react';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';
import { BrowserRouter as Router ,Route,Redirect } from 'react-router-dom'

//auth
import { Security, SecureRoute, ImplicitCallback,withAuth } from '@okta/okta-react';

import Login from './auth/octa/login'
import Profile from './containers/profile'
// components
import BaseRouter from './router.js'
import Navigation from './layout/Navigation'

// apollo client setup
const client = new ApolloClient({
    uri: 'http://35.154.49.7:4000/graphql'
});

function onAuthRequired({history}){
  history.push('/login');
}



class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
       authenticated: null
     }
  }

  checkAuthentication=async()=> {
    try{
        const authenticated = await this.props.auth.isAuthenticated();
        if (authenticated !== this.state.authenticated) {
          this.setState({ authenticated });
          }
        }
    catch(err){console.log(err)}
  }

  componentDidMount=async()=> {
  this.checkAuthentication();
  }

  componentDidUpdate=async()=> {
  this.checkAuthentication();
  }



  render() {
    console.log(this.state.authenticated)
    return (

        <ApolloProvider client={client}>
          <Router>
          <React.Fragment>

          <Security issuer='https://dev-832397.okta.com/oauth2/default'
                    client_id='0oacki8i0GaVo6ZfM356'
                    redirect_uri={window.location.origin + '/implicit/callback'}
                    onAuthRequired={onAuthRequired} >
              <Navigation />
              <SecureRoute path='/profile' component={Profile} />
              <Route path='/login' render={() => <Login baseUrl='https://dev-832397.okta.com' />} />
              <Route path='/implicit/callback' component={ImplicitCallback} />
              <BaseRouter {...this.props} />
            </Security>
            </React.Fragment>
          </Router>
        </ApolloProvider>
    );
  }
}
export default withAuth(App);
