import React, { Fragments } from 'react';
import { graphql } from 'react-apollo';
import { getBookQuery } from '../queries/queries';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';

const styles = {
  appBar: {
    position: 'relative',
  },
  flex: {
    flex: 1,
  },
};

function Transition(props) {
  return <Slide direction="up" {...props} />;
}


class BookDetails extends React.Component {
  constructor(props){
      super(props)
  }

  state = {
      open: false,
  };
  handleClickOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  displayBookDetails(){
      var book = this.props.data.book;
        try{
        if(this.props.bookId ){
          return(
              <div className="site-font">
                  <h2>{ book.title }</h2>

                  <h4>genre : </h4>
                    {book.genre!==null ?
                      book.genre.map(item =>{return <p key="item1">{ item }</p>}):
                      <p key="item1">no genre found</p>}

                  <h4>author : </h4>
                  {book.author!==null ?
                     book.author.map(item => {return <p key="item2">{ item }</p>}) :
                      <p key="item2">no detail about author</p>}

                  <h4>description</h4>
                  {book.description!==null ?
                     <p key="item3">{book.description}</p> :
                     <p key="item3"> no description</p> }
              </div>
          )
        }
    }
    catch (err){
      console.log(err);
    }
  }

  createModal(id){
    const { classes } = this.props;
    var book = this.props.data.book;
    return(
      <div>
          <Button variant="outlined" color="primary" onClick={this.handleClickOpen}>
            Open full-screen dialog
          </Button>
          
          <Dialog
            fullScreen
            open={this.state.open}
            onClose={this.handleClose}
            TransitionComponent={Transition}
          >
            <AppBar className={classes.appBar}>
              <Toolbar>
                <IconButton
                color="inherit"
                onClick={this.handleClose}
                aria-label="Close">
                  <CloseIcon />
                </IconButton>
                <Typography
                variant="h6"
                color="inherit"
                className={classes.flex}>
                  {book.title}
                </Typography>
                <Button color="inherit" onClick={this.handleClose}>
                  Add To Shelf
                </Button>
              </Toolbar>
            </AppBar>
            <Typography
            variant="h1"
            color="inherit"
            className={classes.flex}
            >
              {book.title} by {book.author}
            </Typography>
            <Divider />
            <Typography
            variant="h3"
            color="inherit"
            className={classes.flex}
            >
              {book.genre}
            </Typography>
            <Divider />

          </Dialog>
      </div>
    )
  }



  render(){
      return(
          <Fragments>
              { this.createModal(this.props.bookId) }
          </Fragments>
      );
  }
}

export default graphql(getBookQuery, {
    options: (props) => {
        return {
            variables: {
                id: props.bookId
            }
        }
    }
});withStyles(styles)(BookDetails);
