import React, {Fragments} from "react";
// material-ui components
import withStyles from "@material-ui/core/styles/withStyles";
// core components
import Card from "../Card/Card.jsx";
import CardBody from "../Card/CardBody.jsx";
import CardHeader from "../Card/CardHeader.jsx";
import Button from "../CustomButtons/Button.jsx";
import PropertiesCard from "../body/PropertiesCard"
import BookDetails from "../BookDetails"
import imagesStyles from "../../assets/jss/material-kit-react/imagesStyles.jsx";

import { cardTitle } from "../../assets/jss/material-kit-react.jsx";

const img = require("./W.jpeg")
const style = {
  ...imagesStyles,
  cardTitle,
  textMuted: {
    color: "#6c757d"
  },
};

class Cards extends React.Component {
  constructor(props){
    super(props)

    this.state= {
      showDetails:false
    }

    this.handleClick = this.handleClick.bind(this)
  }

  mouseHover = (event) => {
      return (
        <h1></h1>
      )
  }

  handleClick(data){
    return(  <BookDetails book={data} /> )
  }

  render() {
    const { classes } = this.props;
    var data = this.props;
    return (
      <div>
      <Card style={{width: "20rem" , position: "relative"}}>
        <CardHeader color="warning" onMouseOver={this.mouseHover()}>featured</CardHeader>
        <CardBody>
          <h4 className={classes.cardTitle}>{data.title}</h4>
          <p>
            {data.ts}
          </p>
          <Button
          color="primary"
          onClick={(e) => {this.handleClick(data)}}
          >See Full Post
          </Button>
        </CardBody>
      </Card>

      </div>
    );
  }
}


export default withStyles(style)(Cards);
