import React, { Component } from 'react';
import ApolloClient from 'apollo-boost';
import { ApolloProvider } from 'react-apollo';
import { BrowserRouter as Router ,Route,Redirect } from 'react-router-dom'
import firebase from "firebase"


// components
import BaseRouter from './router.js'
import Navigation from './layout/Navigation'


//auth
var config = {
 apiKey: "AIzaSyBdD-dZnTara6z3jjKOENeqWQJz8Lk53jQ",
 authDomain: "fir-auth-b1ee0.firebaseapp.com",
};
firebase.initializeApp(config);
// apollo client setup
const client = new ApolloClient({
    uri: 'http://35.154.49.7:4000/graphql'
});



class App extends Component {

  state = {
    isSignedIn: false,
    UserEmail:null,
    UserEmailvarified: false,
    UserDisplayName:null,
    UserPhoneNumber:null,
    UserPhotoUrl:null

  }

  auth = (data)=>  this.setState({authenticated:data})

  componentDidMount = () => {
             firebase.auth().onAuthStateChanged(user => {
                      this.setState({
                        isSignedIn: !!user,
                        UserEmail:user.email,
                        UserEmailvarified:user.emailVerified,
                        UserDisplayName:user.displayName,
                        UserPhoneNumber:user.phoneNumber,
                        UserPhotoUrl:user.photoURL
                       })
                       console.log("checking"+user);
                    })



            if(this.state.isSignedIn===false) {
              this.setState({
                UserEmail:null,
                UserEmailvarified:false,
                UserDisplayName:null,
                UserPhoneNumber:null,
                UserPhotoUrl:null
              })

          }
}

  render() {

    return (

        <ApolloProvider client={client}>
          <Router>
          <React.Fragment>
              <Navigation {...this.state}/>
              <BaseRouter {...this.state} />
            </React.Fragment>
          </Router>
        </ApolloProvider>
    );
  }
}
export default App
